
public class Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String choice = "D";

		switch (choice) {
		case "A":
			System.out.println("case A is chosed");
			break;

		case "B":
			System.out.println("case B is chosed");
			break;

		case "C":
			System.out.println("case C is chosed");
			break;

		case "D":
			System.out.println("case D is chosed");
			System.out.println("case D1 is chosed");

			break;

		default:
			System.out.println("option did not matched with the choice given");
		}

	}

}
