
public class InplicitTypeCast {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		byte a = 40;
		int x = a;
		char c = 'a';
		int value = c;
		System.out.println(value);
		System.out.println(a);

		int i = 888;
		double d = i;
		float f = i;
		System.out.println(d);
		System.out.println(f);

	}

}
